document.addEventListener("DOMContentLoaded",()=>{{const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))}),window.addEventListener("resize",()=>{991.98<window.innerWidth&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))})}window.addEventListener("scroll",function(){const e=document.querySelector("nav");1<=window.scrollY?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")})});